import React, { Component } from 'react';
import { Button } from 'reactstrap';

import './App.css';

class Modal extends Component {
  state = {
    visible: true
  }
  toggleAlert(){
    this.setState({
      visible: ! this.state.visible
    });
  }
  render() {
    return (
      <div className="Modal">
      <Button>Edit</Button>
      </div>
    );
  }
}

export default Modal;
