import React, { Component } from 'react';
import { Button , Modal , ModalHeader , ModalFooter , ModalBody , ModalDialog} from 'reactstrap';

import './App.css';

class App extends Component {
  state = {
    visible: true,
    modalIsOpen: false
  }

  toggleModal(){
    this.setState(
      { modalIsOpen : ! this.state.modalIsOpen }
    );
  }

  render() {
    return (
      <div className="App">
      <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

      <Modal isOpen={this.state.modalIsOpen} dialogClassName="ModalStyle">
        <ModalHeader className="ModalStyleHeader" toggle={this.toggleModal.bind(this)} >Modal Title</ModalHeader>
        <ModalBody>Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae
              unde commodi aspernatur enim, consectetur. Cumque deleniti
              temporibus ipsam atque a dolores quisquam quisquam adipisci
              possimus laboriosam. Quibusdam facilis doloribus debitis! Sit
              quasi quod accusamus eos quod. Ab quos consequuntur eaque quo rem!
              Mollitia reiciendis porro quo magni incidunt dolore amet atque
              facilis ipsum deleniti rem!</ModalBody>
        <ModalFooter>
          <Button color="primary">Sign Up</Button>
          <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
        </ModalFooter>
      </Modal>
      </div>
    );
  }
}

export default App;
