import React, { Component } from 'react';
import { Button , Modal , Form , Label , Col ,Input , FormGroup , FormText , ModalHeader , ModalFooter , ModalBody , ModalDialog} from 'reactstrap';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class CreateModal extends Component {
  constructor(props) {
  super(props);
  this.state = {
    startDate: new Date()
  };
  this.handleChange = this.handleChange.bind(this);
}

handleChange(date) {
  this.setState({
    startDate: date
  });
}
state = {
  visible: true,
  modalIsOpen: false
}

toggleModal(){
  this.setState(
    { modalIsOpen : ! this.state.modalIsOpen }
  );
}
  render() {
    return (
      <div className="CreateModal">
      <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

<Modal size='lg' isOpen={this.state.modalIsOpen} >
<ModalHeader toggle={this.toggleModal.bind(this)} >Edit Issue: Project Name</ModalHeader>
<ModalBody>

</ModalBody>
<ModalFooter>
<Button color="primary">Submit</Button>
<Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
</ModalFooter>
</Modal>
      </div>
    );
  }
}

export default CreateModal;
