import React, { Component } from 'react';
import { Button , Modal , Form , Label , Col ,Input , FormGroup  , ModalHeader , ModalFooter , ModalBody  } from 'reactstrap';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class CreateModal extends Component {
  constructor(props) {
  super(props);
  this.state = {
    name: '',
    category: ''
  }
  this.handleChange = this.handleChange.bind(this);
}

handleChange(date) {
  this.setState({
    startDate: date
  });
}
state = {
  visible: true,
  modalIsOpen: false
}

toggleModal(){
  this.setState(
    { modalIsOpen : ! this.state.modalIsOpen }
  );
}

changeHandler = e => {
  this.setState({[e.target.name]: e.target.value})
}
submitHandler = e =>{
  e.preventDefault()
  console.log(this.state)
}

  render() {
    const { name, category} = this.state
    return (
      <div className="CreateModal">
        <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

        <Modal size='lg' isOpen={this.state.modalIsOpen} >
          <ModalHeader toggle={this.toggleModal.bind(this)} >Create Plan</ModalHeader>
          <ModalBody>
              <form onSubmit={this.submitHandler}>
              <div>
                <input type="text" name="name" value={name} onChange={this.changeHandler}/>
              </div>
              <div>
                <input type="text" name="category" value={category} onChange={this.changeHandler} />
              </div>
                <Button>Submit</Button>
              </form>
          </ModalBody>
          <ModalFooter>

            <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default CreateModal;
