import React, { Component } from 'react';
import { Button , Modal , Form , Label , Col ,Input , FormGroup , FormText , ModalHeader , ModalFooter , ModalBody , ModalDialog} from 'reactstrap';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class CreateModal extends Component {
  constructor(props) {
  super(props);
  this.state = {
    startDate: new Date()
  };
  this.handleChange = this.handleChange.bind(this);
}

handleChange(date) {
  this.setState({
    startDate: date
  });
}
state = {
  visible: true,
  modalIsOpen: false
}

toggleModal(){
  this.setState(
    { modalIsOpen : ! this.state.modalIsOpen }
  );
}
  render() {
    return (
      <div className="CreateModal">
        <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

        <Modal size='lg' isOpen={this.state.modalIsOpen} >
          <ModalHeader toggle={this.toggleModal.bind(this)} >Create Plan</ModalHeader>
          <ModalBody>
              <Form>
                <FormGroup row>
                  <Label for="Summary" sm={2}>Name</Label>
                  <Col sm={10}>
                    <Input name="Summary" id="Summary" />
                  </Col>
                </FormGroup>

                <FormGroup row>
                  <Label for="Priority" sm={2}>Priority</Label>
                  <Col sm={10}>
                      <Input type="select" name="select" id="Priority">
                        <option>Very High</option>
                        <option>High</option>
                        <option>Medium</option>
                        <option>Low</option>
                        <option>Very Low</option>
                      </Input>
                  </Col>
                </FormGroup>
                <FormGroup row>
                  <Label for="Comment" sm={2}>Add Comment</Label>
                  <Col sm={10}>
                    <Input type="textarea" name="text" placeholder="Write a comment" />
                  </Col>
                </FormGroup>
                <FormGroup row>
                  <Label for="Comment" sm={2}>Description</Label>
                  <Col sm={10}>
                    <Input type="textarea" name="text" placeholder="Write a comment" />
                  </Col>
                </FormGroup>
                <FormGroup row>
                  <Label for="Issue" sm={2}>Data</Label>
                  <Col sm={10}>
                  <DatePicker
                      selected={this.state.startDate}
                      onChange={this.handleChange}
                      showTimeSelect
                      timeFormat="HH:mm"
                      timeIntervals={15}
                      dateFormat="MMMM d, yyyy h:mm aa"
                      timeCaption="time"
                  />
                  </Col>
                </FormGroup>
              </Form>
          </ModalBody>
          <ModalFooter>
            <Button color="primary">Submit</Button>
            <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default CreateModal;
