import React, { Component } from 'react';
import { Col, Row , Button , Modal , ModalHeader , ModalFooter , ModalBody , ModalDialog, Form} from 'reactstrap';

import './App.css';

class App extends Component {
  state = {
    visible: true,
    modalIsOpen: false
  }

  toggleModal(){
    this.setState(
      { modalIsOpen : ! this.state.modalIsOpen }
    );
  }

  render() {
    return (
      <div className="App">
      <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

      <Modal size='lg' isOpen={this.state.modalIsOpen} >
        <ModalHeader toggle={this.toggleModal.bind(this)} >Edit Issue: Project Name</ModalHeader>
        <ModalBody>

        <Form>
  <Form.Group as={Row} controlId="formHorizontalEmail">
    <Form.Label column sm={2}>
      Email
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="email" placeholder="Email" />
    </Col>
  </Form.Group>
  </Form>


        </ModalBody>
        <ModalFooter>
          <Button color="primary">Sign Up</Button>
          <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
        </ModalFooter>
      </Modal>
      </div>
    );
  }
}

export default App;
