import React, { Component } from 'react';
import { Button , Modal , ModalHeader , ModalFooter , ModalBody , ModalDialog} from 'reactstrap';

import './App.css';

class App extends Component {
  state = {
    visible: true,
    modalIsOpen: false
  }

  toggleModal(){
    this.setState(
      { modalIsOpen : ! this.state.modalIsOpen }
    );
  }

  render() {
    return (
      <div className="App">
      <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

      <Modal size='lg' isOpen={this.state.modalIsOpen} >
        <ModalHeader toggle={this.toggleModal.bind(this)} >Edit Issue: Project Name</ModalHeader>
        <ModalBody>


              <div className="row">
  <div className="col-sm-4"><label className="text">Summary:</label></div>
  <div className="col-sm-8">  <input type="text" value={this.state.name} onChange={this.handleChangeName} className="form-control" /></div>
  </div>



        </ModalBody>
        <ModalFooter>
          <Button color="primary">Sign Up</Button>
          <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
        </ModalFooter>
      </Modal>
      </div>
    );
  }
}

export default App;
