import React, { Component } from 'react';
import { Button , Modal , Form , Label , Col ,Input , FormGroup , FormText , ModalHeader , ModalFooter , ModalBody , ModalDialog} from 'reactstrap';
import DatePicker from "react-datepicker";
import './App.css';

class App extends Component {
  state = {
    visible: true,
    modalIsOpen: false
  }

  toggleModal(){
    this.setState(
      { modalIsOpen : ! this.state.modalIsOpen }
    );
  }

  render() {
    return (
      <div className="App">
      <Button color="primary" onClick={this.toggleModal.bind(this)}>Edit</Button>

      <Modal size='lg' isOpen={this.state.modalIsOpen} >
        <ModalHeader toggle={this.toggleModal.bind(this)} >Edit Issue: Project Name</ModalHeader>
        <ModalBody>
        <Form>
          <FormGroup row>
            <Label for="Summary" sm={2}>Summary*</Label>
            <Col sm={10}>
              <Input name="Summary" id="Summary" />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Label for="Issue" sm={2}>Data</Label>
            <Col sm={10}>
            <DatePicker
  selected={this.state.startDate}
  onChange={this.handleChange}
/>
            </Col>
          </FormGroup>
          <FormGroup row>
            <Label for="Priority" sm={2}>Priority</Label>
            <Col sm={10}>
                <Input type="select" name="select" id="Priority">
                  <option>Very High</option>
                  <option>High</option>
                  <option>Medium</option>
                  <option>Low</option>
                  <option>Very Low</option>
                </Input>
            </Col>
          </FormGroup>
          <FormGroup row>
            <Label for="Comment" sm={2}>Add Comment</Label>
            <Col sm={10}>
              <Input type="textarea" name="text" placeholder="Write a comment" />
            </Col>
          </FormGroup>

        </Form>


        </ModalBody>
        <ModalFooter>
          <Button color="primary">Submit</Button>
          <Button color="secondary" onClick={this.toggleModal.bind(this)}>Cancel</Button>
        </ModalFooter>
      </Modal>
      </div>
    );
  }
}

export default App;
